def ResponseMessage = messageExchange.response.responseContent def jsonSlurper = new JsonSlurper().parseText(ResponseMessage)
PerformanceScore = jsonSlurper.lighthouseResult.categories.performance.score log.info "PerformanceScore is : " + PerformanceScore def expected = 0.50
try { assert PerformanceScore >= expected log.info "performanceScore_returned_from_lightHouse : " + PerformanceScore log.info "expected : " + expected } catch (AssertionError e) { log.info "Performance_score comparison assertion failed" throw e }

