def ResponseMessage = messageExchange.response.responseContent def jsonSlurper = new JsonSlurper().parseText(ResponseMessage)
def id=jsonSlurper.id def author=jsonSlurper.author def fname=jsonSlurper.fileName def ver=jsonSlurper.version def owner=jsonSlurper.owner def recomend=jsonSlurper.recommended def lang=jsonSlurper.languages def category=jsonSlurper.categories def printer=jsonSlurper.models
try { assert id == "dipanjana_k" assert author == "dkundu" assert fname == "dipanjana-through-API.exe" assert ver == "5.3.0.0" assert owner == "pcote" assert recomend == "true" assert lang == ["en","es"] assert category == ["All-In-One","Color Laser"] assert printer == ["Lexmark CX310"] } catch (AssertionError e) { log.info "Document id assertion failed" throw e }

import groovy.json.JsonSlurper

log.info "Validating new Push"

def ResponseMessage = messageExchange.response.responseContent
def jsonSlurper = new JsonSlurper().parseText(ResponseMessage)

try{
//jsonSlurper.each{

//log.info "Inside TRY" + jsonSlurper.driverList(0).id
	for (int i = 0; i < jsonSlurper.driverList.size(); i++)
	{ 
		if (jsonSlurper.driverList[i].id == "dipanjana_k")
		{log.info "Inside IF"
		log.info " jsonSlurper.driverList[i].id : " + jsonSlurper.driverList[i].id
		log.info "Entry Found"
		break
		}
	}
}

catch (AssertionError e)
{
	log.info "New entry not found"
	throw e
}

